/**
 * n8n - Function node
 * Extracts notes from HubSpot engagements API response and converts to Markdown string.
 * Handles the actual API response structure from HubSpot engagements endpoint.
 */

return items.map(item => {
  // HubSpot engagements API returns data in 'results' array
  const engagements = item.json?.results || [];

  // Filter for NOTE type engagements and extract note data
  const notes = engagements
    .filter(engagement => engagement.engagement?.type === 'NOTE')
    .map(engagement => ({
      dateAdded: new Date(engagement.engagement?.createdAt || engagement.engagement?.lastUpdated).toISOString(),
      body: engagement.associations?.associatedVids ? 
        engagement.metadata?.body || engagement.engagement?.bodyPreview || 'No content' :
        engagement.metadata?.body || engagement.engagement?.bodyPreview || 'No content'
    }))
    .sort((a, b) => new Date(b.dateAdded) - new Date(a.dateAdded)); // Sort by newest first

  // Build the combined note string
  const combinedNotes = notes.length > 0 ? 
    notes
      .map((note, idx) => 
        `Note#${idx + 1}:\n${note.dateAdded}\n${note.body || 'No content'}`)
      .join('\n\n') : 
    'No notes found';

  // Output item with notes field
  return {
    json: {
      notes: combinedNotes,
      noteCount: notes.length,
      contactId: item.json?.contactId || 'Unknown'
    }
  };
});

/**
 * n8n Function node â€“ Group eâ€‘mails by thread + add dateTime
 */

const REP_ADDRESSES = [
  'muhammadriam00268@gmail.com',
  'ahmedriam00268@gmail.com'  // Added this email
].map(a => a.toLowerCase());

/* ---------- helpers ---------- */
function decodeBody(payload) {
  if (payload?.body?.data) return b64(payload.body.data);

  if (Array.isArray(payload?.parts)) {
    const part =
      payload.parts.find(p => p.mimeType === 'text/plain' && p.body?.data) ??
      payload.parts.find(p => p.mimeType === 'text/html'  && p.body?.data);
    if (part) return b64(part.body.data);
  }
  return '';
}

function b64(str) {
  const normalised = str.replace(/-/g, '+').replace(/_/g, '/');
  return Buffer.from(normalised, 'base64').toString('utf8');
}

function whoIsSender(fromHeader) {
  const match = fromHeader.match(/<([^>]+)>/);
  const email = (match ? match[1] : fromHeader).trim().toLowerCase();
  return REP_ADDRESSES.includes(email) ? 'rep' : 'customer';
}

/** Convert Gmail internalDate (ms since epoch) âžœ ISOâ€‘8601 in Australia/Sydney */
function formatDateTime(internalDate) {
  const date = new Date(Number(internalDate));
  return new Intl.DateTimeFormat('en-AU', {
    timeZone: 'Australia/Sydney',
    year:   'numeric',
    month:  '2-digit',
    day:    '2-digit',
    hour:   '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
  }).format(date)
    .replace(',', '')
    .replace(/(\d{2})\/(\d{2})\/(\d{4})/, '$3-$2-$1')
    .replace(' ', 'T') + '+10:00';
}

/* ---------- main ---------- */
const rawItems = $input.all();
const threads = new Map();
const orderedThreads = [];

for (const { json } of rawItems) {
  const threadId = json.threadId;
  if (!threads.has(threadId)) {
    const newThread = { threadId, emails: [] };
    threads.set(threadId, newThread);
    orderedThreads.push(newThread);
  }

  const emailObj = {
    dateTime: formatDateTime(json.internalDate),
    from:     whoIsSender(json.From ?? json.from ?? ''),
    message:  decodeBody(json.payload),
  };

  threads.get(threadId).emails.push(emailObj);
}

return orderedThreads.map(t => ({ json: t }));
